import 'dart:convert';

import 'package:flutter/material.dart';
import 'package:material_segmented_control/material_segmented_control.dart';

// Services
import '../services/pit_scouting_service.dart';

// Screens
import '../screens/pit_scouting_detail_screen.dart';

// Models
import '../models/pit_scouting_team.dart';

class PitScoutingScreen extends StatefulWidget {
  var teamMode = false;

  @override
  _PitScoutingScreenState createState() => _PitScoutingScreenState();
}

class _PitScoutingScreenState extends State<PitScoutingScreen> {
  PitScoutingService pitScoutingService = PitScoutingService();

  var isLoading = false;

  int _currentSelection = 0;
  List<PitScoutingTeam> newTeamList = [
    PitScoutingTeam(
      scoutName: "takÄ±m 1",
      teamName: "Sneaky Snakes",
      teamNo: 7285,
      chassisType: Chassis.X,
      climbing: true,
      imageProcessing: true,
      shooterType: Shooter.OneWheel,
      hoodType: Hood.Y,
      intake: true,
      funnelType: Funnel.Z,
      maxBalls: 3,
      autonomous: true,
      autonomousComment: "Very nice",
      climbingComment: "Cool",
      comment: "Nice Device",
      imageProcessingType: ImageProcessing.Limelight,
      intakeType: Intake.Y,
      extra: "Nothing",
      imageString:
          "",
    ),
    PitScoutingTeam(
      status: Status.Synced,
      scoutName: "takÄ±m 2",
      teamName: "Antalya Tigers",
      teamNo: 3829,
      chassisType: Chassis.Y,
      climbing: false,
      imageProcessing: true,
      shooterType: Shooter.OneWheel,
      hoodType: Hood.Y,
      intake: true,
      funnelType: Funnel.Z,
      maxBalls: 3,
      autonomous: true,
      autonomousComment: "Very nice",
      comment: "Nice Device",
      imageProcessingType: ImageProcessing.Limelight,
      intakeType: Intake.Y,
      extra: "Nothing",
      imageString:
          "",
    ),
  ];
  List<PitScoutingTeam> teamList = [];

  Future getTeams() async {
    setState(() {
      isLoading = true;
    });
    var teams = await pitScoutingService.getTeams();
    teams.forEach((teamMap) {
      var team = PitScoutingTeam().unmapTeam(teamMap);
      teamList.add(team);
    });
    setState(() {
      isLoading = false;
    });
  }

  @override
  void initState() {
    getTeams();
    super.initState();
  }

  @override
  Widget build(BuildContext context) {
    final deviceHeight = MediaQuery.of(context).size.height;
    final deviceWidth = MediaQuery.of(context).size.width;

    Widget _createPitScoutingListTile(PitScoutingTeam team, bool isNew) {
      return GestureDetector(
        onTap: () {
          Navigator.of(context).pushNamed(
            PitScoutingDetailScreen.routeName,
            arguments: team,
          );
        },
        child: ListTile(
          leading: CircleAvatar(
            backgroundImage: MemoryImage(
              base64Decode(team.imageString),
            ),
          ),
          title: Text(team.scoutName),
          subtitle: Text("${team.teamNo} - ${team.teamName}"),
          trailing: Row(
            mainAxisSize: MainAxisSize.min,
            children: [
              Text(
                !isNew
                    ? team.status == Status.Synced
                        ? "Synced"
                        : "Unsynced"
                    : "New",
                style: TextStyle(fontWeight: FontWeight.bold),
              ),
              SizedBox(
                width: deviceWidth * 0.03,
              ),
              CircleAvatar(
                backgroundColor: !isNew
                    ? team.status == Status.Synced
                        ? Colors.green
                        : Colors.red
                    : Colors.blue[800],
                child: Icon(
                  !isNew
                      ? team.status == Status.Synced
                          ? Icons.done
                          : Icons.close
                      : Icons.new_releases,
                  color: Colors.white,
                ),
              ),
            ],
          ),
        ),
      );
    }

    Map<int, Widget> _children = {
      0: Text('      New      '),
      1: Text('      Saved       '),
    };

    return Container(
      child: isLoading
          ? Center(
              child: CircularProgressIndicator(),
            )
          : Column(
              children: [
                SizedBox(
                  height: deviceHeight * 0.02,
                ),
                MaterialSegmentedControl(
                  children: _children,
                  selectionIndex: _currentSelection,
                  borderColor: Colors.grey,
                  selectedColor: Colors.blue,
                  unselectedColor: Colors.white,
                  borderRadius: 8.0,
                  onSegmentChosen: (index) {
                    setState(() {
                      _currentSelection = index;
                    });
                  },
                ),
                _currentSelection == 1
                    ? ListView.builder(
                        shrinkWrap: true,
                        itemBuilder: (ctx, i) {
                          return _createPitScoutingListTile(teamList[i], false);
                        },
                        itemCount: teamList.length,
                      )
                    : ListView.builder(
                        shrinkWrap: true,
                        itemBuilder: (ctx, i) {
                          return _createPitScoutingListTile(
                              newTeamList[i], true);
                        },
                        itemCount: newTeamList.length,
                      )
              ],
            ),
    );
  }
}
